import { useAuthenticationContext } from '../hooks/useAuthenticationContext'
import { useAdminAuthenticationContext } from '../hooks/useAdminAuthenticationContext'

const ProtectedRoute = ({ children, requireAdmin = false }) => {
  const { user } = useAuthenticationContext()
  const { admin } = useAdminAuthenticationContext()

  if (requireAdmin && !admin) {
    // Prevent access to admin routes if not admin
    return <p>Access Denied: Admins Only</p> // Optional message
  }

  if (!requireAdmin && !user) {
    // Prevent access to user routes if not authenticated
    return <p>Access Denied: Please log in</p> // Optional message
  }

  if (requireAdmin && user) {
    // Prevent users from accessing admin routes
    return <p>Access Denied: Admins Only</p>
  }

  return children
}

export default ProtectedRoute
