import { useState, useEffect } from 'react'
import { useAdminSignup } from '../hooks/useAdminSignup'
import { useNavigate } from 'react-router-dom'

const AdminRegister = () => {
  const [idNumber, setIdNumber] = useState('')
  const [password, setPassword] = useState('')
  const [totpSetup, setTotpSetup] = useState(null) // state for TOTP setup info
  const { adminSignup, isLoading, error } = useAdminSignup()
  const navigate = useNavigate()

  const handleSignup = async (e) => {
    e.preventDefault()
    const response = await adminSignup(idNumber, password)
    if (response.ok) {
      // Retrieve TOTP setup information from localStorage
      const setupInfo = JSON.parse(localStorage.getItem('totpSetup'))
      setTotpSetup(setupInfo)

      // Optionally clear TOTP setup from localStorage for security
      localStorage.removeItem('totpSetup')
    }
  }

  return (
    <div>
      <h1
        style={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          fontSize: '50px',
        }}
      >
        Register
      </h1>
      <div
        style={{
          display: 'flex',
          justifyContent: 'center',
          height: '100vh',
          width: '100vw',
          marginTop: '50px',
        }}
      >
        <form>
          <label>
            ID Number:
            <input
              style={{ width: '450px' }}
              type='text'
              onChange={(e) => setIdNumber(e.target.value)}
              value={idNumber}
            />
          </label>
          <br />
          <label>
            Password:
            <input
              style={{ width: '450px' }}
              type='password'
              onChange={(e) => setPassword(e.target.value)}
              value={password}
            />
          </label>
          <br />
          <div
            style={{
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
            }}
          >
            <button
              style={{
                width: '250px',
              }}
              type='button'
              onClick={handleSignup}
              disabled={isLoading}
            >
              Register
            </button>
          </div>
          {error && <div className='error'>{error}</div>}
        </form>
      </div>

      {/* Display TOTP setup information if available */}
      {totpSetup && (
        <div style={{ textAlign: 'center', marginTop: '20px' }}>
          <h3>Set Up Two-Factor Authentication</h3>
          <p>Scan the QR code below with your authenticator app:</p>
          {totpSetup.qrCodeURL && (
            <img src={totpSetup.qrCodeURL} alt='TOTP QR Code' />
          )}
          <p>
            Or, manually enter the secret key:{' '}
            <strong>{totpSetup.secret}</strong>
          </p>
          <p>
            After setup, return to the login page to complete authentication.
          </p>
        </div>
      )}
    </div>
  )
}

export default AdminRegister
